mapper.select:
select
rpid, mid, url, oid, ctime, root, parent, location, message, like_num, up_action, stat, typ, catch_time, reply_num
from t_bili_reply


        and rpid = #{rpid}


        and mid = #{mid}


        and url = #{url}


        and oid = #{oid}


        and ctime = #{ctime}


        and root = #{root}


        and parent = #{parent}


        and location = #{location}


        and message = #{message}


        and like_num >= #{like}


        and reply_num >= #{replyNum}


        and up_action = #{upAction}


        and stat = #{stat}


        and typ = #{type}


        and catch_time=#{catchTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_bili_reply


        and rpid = #{rpid}


        and mid = #{mid}


        and url = #{url}


        and oid = #{oid}


        and ctime = #{ctime}


        and root = #{root}


        and parent = #{parent}


        and location = #{location}


        and message = #{message}


        and like_num = #{like}


        and up_action = #{upAction}


        and stat = #{stat}


        and typ = #{type}


        and catch_time = #{catchTime}
